"use client"

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getConversations } from '../services/messageApi';
import Chat from '../components/Chat/Chat';
import ChatList from '../components/Chat/ChatList';
import { useSocket } from '../contexts/SocketContext';
import { useAuth } from '../contexts/AuthContext';

const MessagesPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { socket, connected } = useSocket();
  
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Load conversations
  useEffect(() => {
    const loadConversations = async () => {
      try {
        setLoading(true);
        const data = await getConversations();
        setConversations(data);
        setError(null);
      } catch (error) {
        console.error('Error loading conversations:', error);
        setError('Failed to load conversations');
      } finally {
        setLoading(false);
      }
    };

    loadConversations();
  }, []);

  // Socket event handlers pentru actualizarea conversațiilor
  useEffect(() => {
    if (!socket || !connected) return;

    const handleMessageReceived = async (message) => {
      if (message.sender_id === user?.id || message.receiver_id === user?.id) {
        // Reîncărcăm lista de conversații
        try {
          const data = await getConversations();
          setConversations(data);
        } catch (err) {
          console.error('Error refreshing conversations:', err);
        }
      }
    };

    const handleMessageRead = async (message) => {
      if (message.sender_id === user?.id || message.receiver_id === user?.id) {
        // Reîncărcăm lista de conversații pentru a actualiza contoarele de mesaje necitite
        try {
          const data = await getConversations();
          setConversations(data);
        } catch (err) {
          console.error('Error refreshing conversations:', err);
        }
      }
    };

    socket.on('message_received', handleMessageReceived);
    socket.on('message_read', handleMessageRead);

    return () => {
      socket.off('message_received', handleMessageReceived);
      socket.off('message_read', handleMessageRead);
    };
  }, [socket, connected, user?.id]);

  // Filter conversations based on search
  const filteredConversations = conversations.filter(conv =>
    conv.user.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Find the current conversation
  const currentConversation = conversations.find(
    conv => conv.user.id === parseInt(userId)
  )?.user;

  return (
    <div className="flex h-[calc(100vh-60px)] bg-gray-50">
      {/* Left sidebar with conversations */}
      <div className="w-80 bg-white border-r border-gray-200 flex flex-col h-full">
        <div className="p-4 border-b border-gray-200">
          <h1 className="text-xl font-semibold text-gray-900 mb-4">Messages</h1>
          <div className="relative">
            <input
              type="text"
              placeholder="Search conversations..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full bg-gray-100 rounded-full py-2 pl-10 pr-4 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:bg-white"
            />
            <svg
              className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500"
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <circle cx="11" cy="11" r="8"></circle>
              <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
            </svg>
          </div>
        </div>
        
        <div className="flex-1 overflow-y-auto">
          {loading ? (
            <div className="flex justify-center items-center h-full">
              <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-blue-500"></div>
              <span className="ml-2 text-gray-600 text-sm">Loading...</span>
            </div>
          ) : error ? (
            <div className="p-4 text-center text-red-500">{error}</div>
          ) : (
            <ChatList 
              conversations={filteredConversations} 
              activeConversation={parseInt(userId)}
            />
          )}
        </div>
      </div>

      {/* Right side with chat */}
      <div className="flex-1">
        {userId ? (
          <Chat otherUser={currentConversation} />
        ) : (
          <div className="flex items-center justify-center h-full">
            <div className="text-center p-8 bg-white rounded-lg shadow-sm max-w-lg">
              <div className="mx-auto w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mb-4">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="32"
                  height="32"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="text-gray-400"
                >
                  <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
                </svg>
              </div>
              <h2 className="text-xl font-semibold text-gray-900 mb-2">Select a conversation</h2>
              <p className="text-gray-600 mb-6">Choose from your existing conversations or start a new one from a user's profile</p>
              <button 
                onClick={() => navigate('/users')}
                className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
              >
                Find Users
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MessagesPage;
