"use client"

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getConversations } from '../services/messageApi';
import ChatWindow from '../components/Chat/ChatWindow';
import ChatList from '../components/Chat/ChatList';
import { useSocket } from '../contexts/SocketContext';
import { useAuth } from '../contexts/AuthContext';
import "./MessagesPage.css";

const MessagesPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { socket, connected } = useSocket();
  
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Load conversations
  useEffect(() => {
    const loadConversations = async () => {
      try {
        setLoading(true);
        const data = await getConversations();
        setConversations(data);
        setError(null);
      } catch (error) {
        console.error('Error loading conversations:', error);
        setError('Failed to load conversations');
      } finally {
        setLoading(false);
      }
    };

    loadConversations();
  }, []);

  // Socket event handlers pentru actualizarea conversațiilor
  useEffect(() => {
    if (!socket || !connected) return;

    const handleMessageReceived = async (message) => {
      if (message.sender_id === user?.id || message.receiver_id === user?.id) {
        // Reîncărcăm lista de conversații
        try {
          const data = await getConversations();
          setConversations(data);
        } catch (err) {
          console.error('Error refreshing conversations:', err);
        }
      }
    };

    const handleMessageRead = async (message) => {
      if (message.sender_id === user?.id || message.receiver_id === user?.id) {
        // Reîncărcăm lista de conversații pentru a actualiza contoarele de mesaje necitite
        try {
          const data = await getConversations();
          setConversations(data);
        } catch (err) {
          console.error('Error refreshing conversations:', err);
        }
      }
    };

    socket.on('message_received', handleMessageReceived);
    socket.on('message_read', handleMessageRead);

    return () => {
      socket.off('message_received', handleMessageReceived);
      socket.off('message_read', handleMessageRead);
    };
  }, [socket, connected, user?.id]);

  // Filter conversations based on search
  const filteredConversations = conversations.filter(conv =>
    conv.user.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Find the current conversation
  const currentConversation = conversations.find(
    conv => conv.user.id === parseInt(userId)
  )?.user;

  return (
    <div className="messages-page">
      <div className="messages-sidebar">
        <div className="messages-header">
          <h2>Messages</h2>
          <div className="search-container">
            <input
              type="text"
              placeholder="Search conversations..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <svg className="search-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <circle cx="11" cy="11" r="8"></circle>
              <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
            </svg>
          </div>
        </div>
        
        <div className="conversations-container">
          {loading ? (
            <div className="loading-container">Loading conversations...</div>
          ) : error ? (
            <div className="error-container">{error}</div>
          ) : (
            <ChatList 
              conversations={filteredConversations} 
              activeConversation={parseInt(userId)}
            />
          )}
        </div>
      </div>

      <div className="chat-container-wrapper">
        {userId ? (
          <ChatWindow otherUser={currentConversation} />
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-content">
              <svg className="no-chat-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
              </svg>
              <h3>Select a conversation</h3>
              <p>Choose from your existing conversations or start a new one from a user's profile</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MessagesPage;
