import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { useSocket } from '../../contexts/SocketContext';
import { useAuth } from '../../contexts/AuthContext';
import { getMessages, sendMessage, markMessageRead } from '../../services/messageApi';
import { getImageUrl } from '../../utils/imageUtils';
import './Chat.css';

const ChatWindow = ({ otherUser }) => {
  // Hooks și state
  const { userId } = useParams();
  const { user } = useAuth();
  const { socket, connected } = useSocket();
  const messagesEndRef = useRef(null);
  
  // States
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);

  // Încărcarea mesajelor
  useEffect(() => {
    const loadMessages = async () => {
      if (!userId) return;
      
      try {
        setLoading(true);
        const data = await getMessages(userId);
        setMessages(data);
        setError(null);
      } catch (err) {
        console.error('Error loading messages:', err);
        setError('Failed to load messages');
      } finally {
        setLoading(false);
      }
    };

    loadMessages();
  }, [userId]);

  // Socket event handlers
  useEffect(() => {
    if (!socket || !connected || !user?.id) return;

    const handleMessageReceived = (message) => {
      if (message.sender_id === parseInt(userId) || message.receiver_id === parseInt(userId)) {
        setMessages(prev => [...prev, {
          ...message,
          sender: message.sender_id === user.id ? 'you' : 'them'
        }]);
        
        // Mark message as read if we're the receiver
        if (message.sender_id === parseInt(userId) && !message.read_at) {
          markMessageRead(message.id).catch(console.error);
        }
      }
    };

    const handleMessageUpdated = (message) => {
      if (message.sender_id === parseInt(userId) || message.receiver_id === parseInt(userId)) {
        setMessages(prev => prev.map(m => 
          m.id === message.id ? {
            ...message,
            sender: message.sender_id === user.id ? 'you' : 'them'
          } : m
        ));
      }
    };

    const handleMessageDeleted = ({ messageId }) => {
      setMessages(prev => prev.filter(m => m.id !== messageId));
    };

    const handleUserTyping = ({ userId: typingUserId }) => {
      if (typingUserId === parseInt(userId)) {
        setIsTyping(true);
      }
    };

    const handleUserStoppedTyping = ({ userId: typingUserId }) => {
      if (typingUserId === parseInt(userId)) {
        setIsTyping(false);
      }
    };

    // Socket event listeners
    socket.on('message_received', handleMessageReceived);
    socket.on('message_updated', handleMessageUpdated);
    socket.on('message_deleted', handleMessageDeleted);
    socket.on('user_typing', handleUserTyping);
    socket.on('user_stopped_typing', handleUserStoppedTyping);

    // Cleanup
    return () => {
      socket.off('message_received', handleMessageReceived);
      socket.off('message_updated', handleMessageUpdated);
      socket.off('message_deleted', handleMessageDeleted);
      socket.off('user_typing', handleUserTyping);
      socket.off('user_stopped_typing', handleUserStoppedTyping);
    };
  }, [socket, connected, userId, user?.id]);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle typing indicator
  const handleTyping = () => {
    if (!socket || !connected) return;
    
    // Trimite eveniment de typing
    socket.emit('typing', userId);
    
    // Resetează timeout-ul existent
    if (typingTimeout) clearTimeout(typingTimeout);
    
    // Setează un nou timeout pentru a opri indicatorul de scriere
    const timeout = setTimeout(() => {
      socket.emit('stop_typing', userId);
    }, 3000);
    
    setTypingTimeout(timeout);
  };

  // Message handlers
  const handleSendMessage = async (e) => {
    e.preventDefault();
    const messageText = newMessage.trim();
    
    if (!messageText) return;

    try {
      // Oprim indicatorul de typing
      if (socket && connected) {
        socket.emit('stop_typing', userId);
      }
      
      const sentMessage = await sendMessage(userId, messageText);
      setNewMessage('');
      
      // Adăugăm mesajul trimis în state local
      setMessages(prev => [...prev, {
        id: sentMessage.id,
        text: sentMessage.content,
        time: sentMessage.created_at,
        sender: 'you',
        isRead: false,
        isEdited: false
      }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setError('Failed to send message');
    }
  };

  // Render loading state
  if (loading) {
    return <div className="chat-loading">Loading messages...</div>;
  }

  // Render error state
  if (error) {
    return <div className="chat-error">{error}</div>;
  }

  // Main render
  return (
    <div className="chat-container">
      <div className="chat-header">
        <img 
          src={getImageUrl(otherUser?.image)} 
          alt={otherUser?.name}
          className="chat-header-avatar"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = '/default-avatar.png';
          }}
        />
        <div className="chat-header-info">
          <h3 className="chat-header-name">{otherUser?.name}</h3>
          <p className="chat-header-status">
            {otherUser?.isOnline ? 'Online' : 'Offline'}
          </p>
        </div>
      </div>

      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>No messages yet</p>
            <p>Send a message to start the conversation</p>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender === 'you' ? 'sent' : 'received'}`}
            >
              {message.sender === 'them' && (
                <img
                  src={getImageUrl(otherUser?.image)}
                  alt={otherUser?.name}
                  className="message-avatar"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/default-avatar.png';
                  }}
                />
              )}
              <div className="message-content">
                <p>{message.text}</p>
                <div className="message-metadata">
                  <span className="message-time">
                    {new Date(message.time).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </span>
                  {message.isEdited && <span className="edited-tag">(edited)</span>}
                  {message.sender === 'you' && (
                    <span className="message-status">
                      {message.isRead ? 'Read' : 'Sent'}
                    </span>
                  )}
                </div>
              </div>
              {message.sender === 'you' && (
                <img
                  src={getImageUrl(user?.image)}
                  alt={user?.name}
                  className="message-avatar"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/default-avatar.png';
                  }}
                />
              )}
            </div>
          ))
        )}
        {isTyping && (
          <div className="typing-indicator">
            <span></span>
            <span></span>
            <span></span>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyDown={handleTyping}
          placeholder="Type a message..."
          className="message-input"
        />
        <button 
          type="submit" 
          className="send-button"
          disabled={!newMessage.trim()}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
