import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import { getConversations } from '../services/messageApi';
import Chat from '../components/Chat/Chat';
import { ArrowLeft, Search, Settings, Phone, Video } from 'lucide-react';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const { socket, connected } = useSocket();
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Load conversations
  useEffect(() => {
    const fetchChats = async () => {
      try {
        setLoading(true);
        const data = await getConversations();
        
        // VerificÄƒm dacÄƒ avem date valide
        if (!data || !Array.isArray(data)) {
          setConversations([]);
          setError('Nu s-au putut Ã®ncÄƒrca conversaÈ›iile');
          setLoading(false);
          return;
        }
        
        // Nu mai filtrÄƒm conversaÈ›iile, deoarece backend-ul ar trebui sÄƒ returneze doar conversaÈ›iile utilizatorului curent
        setConversations(data);
        setError(null);

        // Check if we have a receiverId in the location state (from profile page)
        const receiverId = location.state?.receiverId;
        
        // If we have a receiverId in state, navigate to that chat
        if (receiverId && !userId) {
          navigate(`/chat/${receiverId}`);
          return;
        }
      } catch (err) {
        console.error('Error fetching chats:', err);
        setError('Failed to load conversations');
      } finally {
        setLoading(false);
      }
    };

    fetchChats();
  }, [userId, navigate, user.id, location.state]);

  // Listen for new messages to update conversations list
  useEffect(() => {
    if (!socket || !connected) return;

    const handleMessageReceived = async (message) => {
      // If this message is relevant to the current user (sender or receiver)
      if (message.sender_id === user.id || message.receiver_id === user.id) {
        // Refresh the conversations list
        try {
          const data = await getConversations();
          if (data && Array.isArray(data)) {
            setConversations(data);
          }
        } catch (err) {
          console.error('Error refreshing conversations after new message:', err);
        }
      }
    };

    socket.on('message_received', handleMessageReceived);

    return () => {
      socket.off('message_received', handleMessageReceived);
    };
  }, [socket, connected, user.id]);

  // Filter conversations based on search query
  const filteredConversations = searchQuery.trim() 
    ? conversations.filter(conv => 
        conv.user.name.toLowerCase().includes(searchQuery.toLowerCase()))
    : conversations;

  if (loading) {
    return <div className="chat-page-loading">Loading chats...</div>;
  }

  if (error) {
    return <div className="chat-page-error">{error}</div>;
  }

  return (
    <div className="chat-page">
      <div className="conversations-sidebar">
        <div className="conversations-header">
          <button 
            className="back-button" 
            onClick={() => navigate('/home')}
            aria-label="Back to Homepage"
          >
            <ArrowLeft size={24} strokeWidth={2.5} />
          </button>
          <h2>Messages</h2>
          <button 
            className="settings-button" 
            onClick={() => navigate('/settings')}
            aria-label="Settings"
          >
            <Settings size={20} />
          </button>
        </div>

        <div className="search-container">
          <div className="search-input-container">
            <Search size={16} className="search-icon" />
            <input 
              type="text"
              placeholder="Search conversations..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
          </div>
        </div>

        <div className="conversations-list">
          {filteredConversations.length === 0 ? (
            <div className="no-conversations">
              <p>No conversations yet</p>
              <p className="no-conversations-hint">Start a chat from a user's profile</p>
            </div>
          ) : (
            filteredConversations.map((conversation) => (
              <div
                key={conversation.user.id}
                className={`conversation-item ${
                  conversation.user.id === parseInt(userId) ? 'active' : ''
                }`}
                onClick={() => navigate(`/chat/${conversation.user.id}`)}
              >
                <div className="conversation-avatar">
                  <img
                    src={conversation.user.image || '/default-avatar.png'}
                    alt={conversation.user.name}
                  />
                  {conversation.user.isOnline && (
                    <span className="online-indicator" />
                  )}
                </div>
                <div className="conversation-details">
                  <div className="conversation-header">
                    <h3>{conversation.user.name}</h3>
                    <span className="conversation-time">
                      {formatMessageTime(conversation.lastMessage.time)}
                    </span>
                  </div>
                  <div className="conversation-preview">
                    <p className="last-message">
                      {conversation.lastMessage.sender === 'you' && <span>You: </span>}
                      {conversation.lastMessage.text}
                    </p>
                    {conversation.unreadCount > 0 && (
                      <span className="unread-count">
                        {conversation.unreadCount}
                      </span>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
      <div className="chat-area">
        {userId ? (
          <>
            <div className="chat-header-controls">
              <div className="current-chat-info">
                <h3>{conversations.find(c => c.user.id === parseInt(userId))?.user.name}</h3>
                <span className={`status-indicator ${conversations.find(c => c.user.id === parseInt(userId))?.user.isOnline ? 'online' : 'offline'}`}>
                  {conversations.find(c => c.user.id === parseInt(userId))?.user.isOnline ? 'Online' : 'Offline'}
                </span>
              </div>
              <div className="chat-actions">
                <button className="action-button" aria-label="Voice call">
                  <Phone size={20} />
                </button>
                <button className="action-button" aria-label="Video call">
                  <Video size={20} />
                </button>
              </div>
            </div>
            <Chat 
              otherUser={conversations.find(c => c.user.id === parseInt(userId))?.user}
            />
          </>
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-icon">ðŸ’¬</div>
            <h3>Your Messages</h3>
            <p>Select a conversation to start chatting</p>
            <p className="no-chat-hint">or find a user to chat with from their profile</p>
          </div>
        )}
      </div>
    </div>
  );
};

// Helper function to format message time
function formatMessageTime(timestamp) {
  const date = new Date(timestamp);
  const now = new Date();
  const diffInDays = Math.floor((now - date) / (1000 * 60 * 60 * 24));
  
  // Today: show time
  if (diffInDays === 0) {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }
  
  // Yesterday: show "Yesterday"
  if (diffInDays === 1) {
    return 'Yesterday';
  }
  
  // Within last 7 days: show day name
  if (diffInDays < 7) {
    return date.toLocaleDateString([], { weekday: 'short' });
  }
  
  // Otherwise: show date
  return date.toLocaleDateString([], { month: 'short', day: 'numeric' });
}

export default ChatPage;
