import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Box, VStack, Input, Button, Text, Flex, HStack, 
  IconButton, Badge, Popover, PopoverTrigger, 
  PopoverContent, Tooltip, Spinner
} from '@chakra-ui/react';
import { useSocket } from '../../contexts/SocketContext';
import axios from 'axios';
import { API_URL } from '../../config/api';

// Corrected icon imports
import { Smile, CornerUpLeft } from 'react-feather';

export function Chat({ receiverId, receiverName }) {
  const { socket } = useSocket();
  const [messages, setMessages] = useState([]);
  const [receiverStatus, setReceiverStatus] = useState('offline');
  const [unreadCount, setUnreadCount] = useState(0);
  const [isWindowFocused, setIsWindowFocused] = useState(true);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [loading, setLoading] = useState(true);
  const [replyTo, setReplyTo] = useState(null);
  
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  const loadMessages = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/messages/${receiverId}`);
      setMessages(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error loading messages:', error);
      setLoading(false);
    }
  }, [receiverId]);

  useEffect(() => {
    const handleFocus = () => setIsWindowFocused(true);
    const handleBlur = () => setIsWindowFocused(false);

    window.addEventListener('focus', handleFocus);
    window.addEventListener('blur', handleBlur);

    // If you have a NotificationService, uncomment this
    // NotificationService.requestPermission();

    return () => {
      window.removeEventListener('focus', handleFocus);
      window.removeEventListener('blur', handleBlur);
    };
  }, []);

  useEffect(() => {
    loadMessages();
  }, [loadMessages]);

  useEffect(() => {
    if (socket) {
      socket.on('user_status', (data) => {
        if (data.userId === receiverId) {
          setReceiverStatus(data.status);
        }
      });

      // Request receiver's status
      socket.emit('get_user_status', receiverId);

      return () => {
        socket.off('user_status');
      };
    }
  }, [socket, receiverId]);

  // Scroll to bottom when messages change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  useEffect(() => {
    if (socket && socket.connected) {
      socket.emit('join_chat', receiverId);
      
      const handleReceiveMessage = (data) => {
        setMessages(prev => {
          // Avoid duplicate messages
          if (prev.some(msg => msg.id === data.id)) {
            return prev;
          }
          return [...prev, data];
        });
        
        if (!isWindowFocused) {
          setUnreadCount(prev => prev + 1);
        }
      };

      const handleUserTyping = (userId) => {
        if (userId === receiverId) {
          setIsTyping(true);
          setTimeout(() => setIsTyping(false), 3000);
        }
      };

      socket.on('receive_message', handleReceiveMessage);
      socket.on('user_typing', handleUserTyping);

      return () => {
        socket.off('receive_message', handleReceiveMessage);
        socket.off('user_typing', handleUserTyping);
        socket.emit('leave_chat', receiverId);
      };
    }
  }, [socket, receiverId, isWindowFocused]);

  useEffect(() => {
    if (isWindowFocused && unreadCount > 0) {
      setUnreadCount(0);
      // Mark messages as read
      messages
        .filter(msg => !msg.read_at && msg.sender_id === receiverId)
        .forEach(msg => {
          if (socket) {
            socket.emit('message_read', {
              messageId: msg.id,
              userId: localStorage.getItem('userId'),
              senderId: msg.sender_id
            });
          }
        });
    }
  }, [isWindowFocused, messages, unreadCount, receiverId, socket]);

  const handleTyping = () => {
    if (socket) {
      socket.emit('typing', receiverId);
    }

    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    typingTimeoutRef.current = setTimeout(() => {
      if (socket) {
        socket.emit('stop_typing', receiverId);
      }
    }, 2000);
  };

  const handleSend = async () => {
    if (!newMessage.trim() || !socket?.connected) return;

    try {
      const messageData = {
        content: newMessage,
        receiverId,
        replyTo: replyTo?.id,
        senderId: localStorage.getItem('userId')
      };

      const response = await axios.post(`${API_URL}/messages`, messageData);
      
      if (socket?.connected) {
        socket.emit('send_message', { ...response.data, receiverId });
        setMessages(prev => [...prev, response.data]);
        setNewMessage('');
        setReplyTo(null);
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const reactions = ['ðŸ‘', 'â¤ï¸', 'ðŸ˜Š', 'ðŸ˜‚', 'ðŸ˜®', 'ðŸ˜¢'];

  const handleReaction = async (messageId, reaction) => {
    try {
      await axios.post(`${API_URL}/messages/${messageId}/reactions`, {
        reaction
      });
      
      if (socket) {
        socket.emit('message_reaction', { messageId, reaction, receiverId });
      }
    } catch (error) {
      console.error('Error adding reaction:', error);
    }
  };

  const handleReply = (message) => {
    setReplyTo(message);
    // Focus on input
    document.querySelector('#messageInput').focus();
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" h="100%">
        <Spinner />
      </Box>
    );
  }

  const MessageBubble = ({ message }) => (
    <Box position="relative" maxW="70%">
      {message.reply_to && (
        <Box 
          borderLeftWidth="2px" 
          borderLeftColor="gray.300" 
          pl={2} 
          mb={1}
          opacity={0.7}
        >
          <Text fontSize="sm">{messages.find(m => m.id === message.reply_to)?.content}</Text>
        </Box>
      )}
      <Box
        bg={message.sender_id === parseInt(localStorage.getItem('userId')) ? 'blue.500' : 'gray.200'}
        color={message.sender_id === parseInt(localStorage.getItem('userId')) ? 'white' : 'black'}
        borderRadius="lg"
        px={3}
        py={2}
      >
        <Text>{message.content}</Text>
        {message.reactions?.length > 0 && (
          <HStack spacing={1} mt={1}>
            {Object.entries(
              message.reactions.reduce((acc, r) => ({
                ...acc,
                [r.reaction]: (acc[r.reaction] || 0) + 1
              }), {})
            ).map(([reaction, count]) => (
              <Badge key={reaction} variant="subtle" colorScheme="gray">
                {reaction} {count}
              </Badge>
            ))}
          </HStack>
        )}
      </Box>
      <HStack position="absolute" right={2} bottom={-6} opacity={0} _groupHover={{ opacity: 1 }} transition="0.2s">
        <Popover placement="top">
          <PopoverTrigger>
            <IconButton size="xs" icon={<Smile />} variant="ghost" />
          </PopoverTrigger>
          <PopoverContent w="auto" p={2}>
            <HStack>
              {reactions.map(reaction => (
                <Box
                  key={reaction}
                  cursor="pointer"
                  p={1}
                  _hover={{ bg: 'gray.100' }}
                  onClick={() => handleReaction(message.id, reaction)}
                >
                  {reaction}
                </Box>
              ))}
            </HStack>
          </PopoverContent>
        </Popover>
        <Tooltip label="Reply">
          <IconButton 
            size="xs" 
            icon={<CornerUpLeft />} 
            variant="ghost"
            onClick={() => handleReply(message)}
          />
        </Tooltip>
      </HStack>
    </Box>
  );

  return (
    <Box h="100%" display="flex" flexDirection="column">
      <Box p={4} bg="gray.100" borderBottom="1px" borderColor="gray.200">
        <Flex justify="space-between" align="center">
          <Text fontWeight="bold">{receiverName}</Text>
          <Badge colorScheme={receiverStatus === 'online' ? 'green' : 'gray'}>
            {receiverStatus}
          </Badge>
        </Flex>
      </Box>

      <VStack flex={1} p={4} spacing={4} overflowY="auto">
        {messages.map((message) => (
          <MessageBubble key={message.id} message={message} />
        ))}
        {isTyping && (
          <Text color="gray.500" fontSize="sm" alignSelf="flex-start">
            {receiverName} is typing...
          </Text>
        )}
        <div ref={messagesEndRef} />
      </VStack>

      {replyTo && (
        <Box p={2} bg="gray.50" borderTop="1px" borderColor="gray.200">
          <Flex justify="space-between" align="center">
            <Text fontSize="sm" color="gray.600">Replying to: {replyTo.content}</Text>
            <IconButton
              icon={<CornerUpLeft size={16} />}
              size="sm"
              variant="ghost"
              onClick={() => setReplyTo(null)}
            />
          </Flex>
        </Box>
      )}

      <Box p={4} borderTop="1px" borderColor="gray.200">
        <Flex>
          <Input
            id="messageInput"
            value={newMessage}
            onChange={(e) => {
              setNewMessage(e.target.value);
              handleTyping();
            }}
            placeholder="Type a message..."
            mr={2}
          />
          <Popover>
            <PopoverTrigger>
              <IconButton icon={<Smile size={20} />} variant="ghost" />
            </PopoverTrigger>
            <PopoverContent p={2}>
              <Flex wrap="wrap" gap={2}>
                {reactions.map((reaction) => (
                  <Button
                    key={reaction}
                    size="sm"
                    variant="ghost"
                    onClick={() => {
                      setNewMessage(prev => prev + reaction);
                      document.querySelector('#messageInput').focus();
                    }}
                  >
                    {reaction}
                  </Button>
                ))}
              </Flex>
            </PopoverContent>
          </Popover>
          <Button ml={2} colorScheme="blue" onClick={handleSend}>
            Send
          </Button>
        </Flex>
      </Box>
    </Box>
  );
}
