import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import io from 'socket.io-client';
import { useAuth } from './AuthContext';
import { API_BASE_URL } from '../config/api';

const SocketContext = createContext();

export function SocketProvider({ children }) {
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token || !user) {
      console.log('[Socket] No authentication token or user');
      return;
    }

    // Initialize socket connection
    const socket = io(API_BASE_URL, {
      path: '/socket.io',
      transports: ['websocket', 'polling'],
      auth: { token },
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      autoConnect: true
    });

    // Connection event handlers
    socket.on('connect', () => {
      console.log('[Socket] Connected to server');
      setConnected(true);
    });

    socket.on('connect_error', (error) => {
      console.error('[Socket] Connection error:', error.message);
      setConnected(false);
    });

    socket.on('disconnect', (reason) => {
      console.log('[Socket] Disconnected:', reason);
      setConnected(false);
    });

    setSocket(socket);

    // Cleanup on unmount
    return () => {
      if (socket) {
        console.log('[Socket] Cleaning up connection');
        socket.removeAllListeners();
        socket.close();
      }
    };
  }, [user]);

  // Message sending helper
  const sendMessage = useCallback((receiverId, content, replyTo = null) => {
    if (socket?.connected) {
      socket.emit('send_message', {
        receiverId,
        content,
        replyTo
      });
    }
  }, [socket]);

  // Message editing helper
  const editMessage = useCallback((messageId, content) => {
    if (socket?.connected) {
      socket.emit('edit_message', {
        messageId,
        content
      });
    }
  }, [socket]);

  // Message deletion helper
  const deleteMessage = useCallback((messageId) => {
    if (socket?.connected) {
      socket.emit('delete_message', {
        messageId
      });
    }
  }, [socket]);

  // Typing notification helpers
  const startTyping = useCallback((receiverId) => {
    if (socket?.connected) {
      socket.emit('typing', receiverId);
    }
  }, [socket]);

  const stopTyping = useCallback((receiverId) => {
    if (socket?.connected) {
      socket.emit('stop_typing', receiverId);
    }
  }, [socket]);

  // Message read helper
  const markMessageRead = useCallback((messageId) => {
    if (socket?.connected) {
      socket.emit('message_read', {
        messageId
      });
    }
  }, [socket]);

  const value = {
    socket,
    connected,
    sendMessage,
    editMessage,
    deleteMessage,
    startTyping,
    stopTyping,
    markMessageRead
  };

  return (
    <SocketContext.Provider value={value}>
      {children}
    </SocketContext.Provider>
  );
}

export const useSocket = () => useContext(SocketContext);
