"use client"

import { useState, useEffect } from "react"
import { Link, useParams, useNavigate } from "react-router-dom"
import {
  ChevronLeft,
  Star,
  MapPin,
  Clock,
  Share2,
  Heart,
  AlertTriangle,
  Check,
  ChevronRight,
  ChevronDown,
  MessageCircle,
  Loader,
  DollarSign,
} from "lucide-react"
import "./RequestDetailsPage.css"
import { getRequestById } from '../services/requestApi'
import { useAuth } from "../contexts/AuthContext"
import { addRequestToFavorites, removeRequestFromFavorites, isRequestFavorited } from "../services/favoriteApi"
import { getImageUrl, handleImageError, getProfileImageUrl } from "../utils/imageUtils"
import { ChakraProvider, Avatar } from '@chakra-ui/react'
import { submitOffer } from '../services/offerApi'

const RequestDetailsPage = () => {
  const { requestId } = useParams()
  const navigate = useNavigate()
  const { user } = useAuth()
  const [activeImageIndex, setActiveImageIndex] = useState(0)
  const [isFavorite, setIsFavorite] = useState(false)
  const [isTogglingFavorite, setIsTogglingFavorite] = useState(false)
  const [showFullDescription, setShowFullDescription] = useState(false)
  const [request, setRequest] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [offerPrice, setOfferPrice] = useState("")
  const [offerDescription, setOfferDescription] = useState("")
  const [offerErrors, setOfferErrors] = useState({})
  const [isSubmittingOffer, setIsSubmittingOffer] = useState(false)
  const [negotiationMode, setNegotiationMode] = useState(null)
  const [offerSuccess, setOfferSuccess] = useState(false)

  // Check if the current user is the owner of the request
  const isOwnRequest = user && request?.user?.id === user.id

  // Fetch request data and favorite status when component mounts
  useEffect(() => {
    const fetchRequestData = async () => {
      try {
        setLoading(true)
        const requestData = await getRequestById(requestId)
        setRequest(requestData)
        setError(null)

        // Check if request is favorited
        if (user) {
          try {
            const token = localStorage.getItem('token')
            const favorited = await isRequestFavorited(requestId, token)
            setIsFavorite(favorited)
          } catch (favoriteError) {
            console.error("Error checking favorite status:", favoriteError)
            // Don't set the main error, just log it
          }
        }
      } catch (error) {
        console.error('Error fetching request:', error)
        setError('Failed to load request details')
      } finally {
        setLoading(false)
      }
    }

    fetchRequestData()
  }, [requestId, user])

  // Toggle favorite
  const toggleFavorite = async () => {
    if (!user) {
      navigate('/login')
      return
    }

    try {
      setIsTogglingFavorite(true)
      const token = localStorage.getItem('token')
      
      if (isFavorite) {
        await removeRequestFromFavorites(requestId, token)
      } else {
        await addRequestToFavorites(requestId, token)
      }
      
      setIsFavorite(!isFavorite)
    } catch (error) {
      console.error('Error toggling favorite:', error)
      alert('A apărut o eroare. Te rugăm să încerci din nou.')
    } finally {
      setIsTogglingFavorite(false)
    }
  }

  // Show loading state
  if (loading) {
    return (
      <div className="loading-container">
        <Loader size={48} className="spinner" />
        <p>Se încarcă detaliile cererii...</p>
      </div>
    )
  }

  // Show error state
  if (error) {
    return (
      <div className="error-container">
        <AlertTriangle size={48} />
        <h2>Eroare</h2>
        <p>{error}</p>
        <button onClick={() => navigate("/requests")} className="back-button">
          Înapoi la cereri
        </button>
      </div>
    )
  }

  // If request not found
  if (!request) {
    return (
      <div className="request-not-found">
        <AlertTriangle size={48} />
        <h2>Cerere negăsită</h2>
        <p>Cererea căutată nu există sau a fost ștearsă.</p>
        <button onClick={() => navigate("/requests")} className="back-button">
          Înapoi la cereri
        </button>
      </div>
    )
  }

  const nextImage = () => {
    if (request.images && request.images.length > 0) {
      setActiveImageIndex((prev) => (prev === request.images.length - 1 ? 0 : prev + 1))
    }
  }

  const prevImage = () => {
    if (request.images && request.images.length > 0) {
      setActiveImageIndex((prev) => (prev === 0 ? request.images.length - 1 : prev - 1))
    }
  }

  // Funcție pentru a reseta formularul de ofertă
  const resetOfferForm = () => {
    setOfferPrice(request?.budget || "")
    setOfferDescription("")
    setOfferErrors({})
    setNegotiationMode(null)
  }

  // Funcție pentru a verifica dacă utilizatorul este un prestator de servicii
  const isServiceProvider = () => {
    return user && user.user_type === 'provider'
  }

  // Funcția pentru trimiterea ofertei, actualizată cu gestionarea negocierii
  const handleSubmitOffer = async (e) => {
    e.preventDefault()
    
    // Validate form
    const errors = {}
    if (!offerPrice.trim()) {
      errors.price = "Prețul este obligatoriu"
    } else if (isNaN(offerPrice) || Number(offerPrice) <= 0) {
      errors.price = "Prețul trebuie să fie un număr pozitiv"
    }
    
    if (!offerDescription.trim()) {
      errors.description = "Descrierea este obligatorie"
    } else if (offerDescription.length < 20) {
      errors.description = "Descrierea trebuie să aibă cel puțin 20 de caractere"
    }
    
    setOfferErrors(errors)
    
    if (Object.keys(errors).length === 0) {
      setIsSubmittingOffer(true)
      
      try {
        // Pregătim datele pentru ofertă
        const offerData = {
          requestId: parseInt(requestId),
          price: parseFloat(offerPrice),
          description: offerDescription,
          negotiationStatus: negotiationMode || 'accept', // Implicit acceptăm prețul dacă nu s-a specificat altceva
          currency: request.currency || 'RON'
        }
        
        // Trimitem oferta către API
        const result = await submitOffer(offerData)
        console.log("Offer submitted successfully:", result)
        
        // Resetăm formularul și afișăm mesajul de succes
        resetOfferForm()
        setOfferSuccess(true)
        
        // După 5 secunde, ascundem mesajul de succes
        setTimeout(() => {
          setOfferSuccess(false)
        }, 5000)
      } catch (error) {
        console.error("Error submitting offer:", error)
        alert("A apărut o eroare la trimiterea ofertei. Te rugăm să încerci din nou.")
      } finally {
        setIsSubmittingOffer(false)
      }
    }
  }

  // Funcție pentru a initia procesul de negociere sau acceptare
  const handleNegotiationStart = (mode) => {
    setNegotiationMode(mode)
    // Dacă modul este "accept", setăm prețul automat la bugetul cererii
    if (mode === 'accept') {
      setOfferPrice(request.budget.toString())
    }
    // Altfel, lăsăm utilizatorul să introducă prețul dorit pentru negociere
  }

  // Adăugăm conținutul pentru sidebar despre negociere
  const renderNegotiationSidebar = () => {
    // Dacă utilizatorul este proprietarul cererii sau dacă cererea nu există, nu afișăm nimic
    if (isOwnRequest || !request || !user) return null
    
    // Dacă utilizatorul nu este un prestator de servicii, nu afișăm opțiunile de ofertă
    if (!isServiceProvider()) {
      return (
        <div className="offer-card info-card">
          <h3>Oportunitate de afaceri</h3>
          <p>Doar prestatorii de servicii pot face oferte pentru această cerere. Creează un cont de prestator pentru a putea răspunde la cereri.</p>
        </div>
      )
    }
    
    // Dacă oferta a fost trimisă cu succes, afișăm mesajul de confirmare
    if (offerSuccess) {
      return (
        <div className="offer-card success-card">
          <div className="success-icon">
            <Check size={32} color="#30c979" />
          </div>
          <h3>Ofertă trimisă cu succes!</h3>
          <p>Utilizatorul va fi notificat despre oferta ta și te va contacta în curând.</p>
          <button 
            className="new-offer-button"
            onClick={() => setOfferSuccess(false)}
          >
            Trimite o altă ofertă
          </button>
        </div>
      )
    }
    
    // Dacă suntem în mod de negociere, afișăm formularul corespunzător
    if (negotiationMode) {
      return (
        <div className="offer-card">
          <h3>
            {negotiationMode === 'accept' 
              ? 'Acceptă cererea'
              : 'Propune un preț'
            }
          </h3>
          
          <form onSubmit={handleSubmitOffer} className="offer-form">
            <div className="form-group">
              <label htmlFor="offerPrice">
                {negotiationMode === 'accept' 
                  ? `Preț acceptat (${request.currency})`
                  : `Preț propus (${request.currency})`
                }
              </label>
              <div className="price-input">
                <DollarSign size={20} />
                <input
                  type="number"
                  id="offerPrice"
                  value={offerPrice}
                  onChange={(e) => setOfferPrice(e.target.value)}
                  placeholder={negotiationMode === 'accept' ? `${request.budget}` : "Introducă prețul propus"}
                  readOnly={negotiationMode === 'accept'}
                  className={offerErrors.price ? "error" : ""}
                />
              </div>
              {offerErrors.price && <span className="error-message">{offerErrors.price}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="offerDescription">Descriere ofertă</label>
              <textarea
                id="offerDescription"
                value={offerDescription}
                onChange={(e) => setOfferDescription(e.target.value)}
                placeholder={
                  negotiationMode === 'accept'
                    ? "Explică cum vei putea onora cererea și de ce ar trebui să te aleagă pe tine..."
                    : "Explică de ce propui acest preț și cum vei onora cererea..."
                }
                rows={4}
                className={offerErrors.description ? "error" : ""}
              />
              {offerErrors.description && <span className="error-message">{offerErrors.description}</span>}
            </div>

            <div className="offer-actions">
              <button 
                type="button" 
                className="cancel-button"
                onClick={resetOfferForm}
              >
                Anulează
              </button>
              <button 
                type="submit" 
                className="submit-offer-button"
                disabled={isSubmittingOffer}
              >
                {isSubmittingOffer ? (
                  <>
                    <Loader size={20} className="spinner" />
                    Se trimite...
                  </>
                ) : (
                  <>
                    <Check size={20} />
                    Trimite oferta
                  </>
                )}
              </button>
            </div>
          </form>
          
          <p className="offer-note">
            Oferta ta va fi trimisă utilizatorului care va putea să o accepte sau să o respingă.
            {negotiationMode === 'negotiate' && ' Dacă utilizatorul este de acord cu prețul propus, vei fi notificat.'}
          </p>
        </div>
      )
    }
    
    // Afișăm opțiunile inițiale de ofertă
    return (
      <div className="offer-card">
        <h3>Ești interesat de această cerere?</h3>
        <p className="request-budget-info">
          <span className="budget-label">Buget disponibil:</span>
          <span className="budget-amount">{request.budget} {request.currency}</span>
        </p>
        
        <div className="negotiation-options">
          <button 
            className="accept-button"
            onClick={() => handleNegotiationStart('accept')}
          >
            <Check size={20} />
            Accept bugetul
          </button>
          <button 
            className="negotiate-button"
            onClick={() => handleNegotiationStart('negotiate')}
          >
            <DollarSign size={20} />
            Propun alt preț
          </button>
        </div>
        
        <p className="offer-hint">
          Alege una dintre opțiuni pentru a face o ofertă pentru această cerere. 
          Poți accepta bugetul propus sau poți negocia un alt preț.
        </p>
      </div>
    )
  }

  return (
    <div className="request-details-page">
      <header className="request-header">
        <button onClick={() => navigate(-1)} className="back-button">
          <ChevronLeft size={24} />
        </button>
        <div className="header-actions">
          <button
            className="action-button"
            onClick={() => window.navigator.share({ title: request.title, url: window.location.href })}
          >
            <Share2 size={20} />
          </button>
          {!isOwnRequest && (
            <button 
              className={`action-button ${isFavorite ? "favorite" : ""}`} 
              onClick={toggleFavorite}
              disabled={isTogglingFavorite}
            >
              {isTogglingFavorite ? (
                <Loader size={20} className="animate-spin" />
              ) : (
                <Heart size={20} fill={isFavorite ? "#ff1d68" : "none"} />
              )}
            </button>
          )}
        </div>
      </header>

      <div className="request-content">
        <div className="request-main">
          {request.images && request.images.length > 0 && (
            <div className="request-gallery">
              <div className="gallery-main">
                <img
                  src={getImageUrl(request.images[activeImageIndex].image_url)}
                  alt={request.title}
                  className="main-image"
                  onError={handleImageError}
                />

                {request.images.length > 1 && (
                  <>
                    <button className="gallery-nav prev" onClick={prevImage}>
                      <ChevronLeft size={24} />
                    </button>
                    <button className="gallery-nav next" onClick={nextImage}>
                      <ChevronRight size={24} />
                    </button>

                    <div className="gallery-indicators">
                      {request.images.map((_, index) => (
                        <button
                          key={index}
                          className={`indicator ${index === activeImageIndex ? "active" : ""}`}
                          onClick={() => setActiveImageIndex(index)}
                        />
                      ))}
                    </div>
                  </>
                )}
              </div>
            </div>
          )}

          <div className="request-header-info">
            <div className="request-meta-top">
              <span className="request-category">{request.category}</span>
              <span className="request-time">{request.postedAt}</span>
            </div>

            <h1 className="request-title">{request.title}</h1>

            <div className="request-meta">
              <div className="meta-item">
                <MapPin size={16} />
                <span>{request.location}</span>
              </div>
              <div className="meta-item">
                <Clock size={16} />
                <span>Termen: {request.deadline}</span>
              </div>
              <div className="meta-item">
                <DollarSign size={16} />
                <span>
                  Buget: {request.budget} {request.currency}
                </span>
              </div>
            </div>
          </div>

          <div className="request-description">
            <h2>Descriere</h2>
            <div className={`description-content ${showFullDescription ? "expanded" : ""}`}>
              <p>{request.description}</p>
            </div>
            {request.description && request.description.length > 300 && (
              <button
                className="show-more-button"
                onClick={() => setShowFullDescription(!showFullDescription)}
              >
                {showFullDescription ? "Arată mai puțin" : "Arată mai mult"}
                <ChevronDown size={16} className={showFullDescription ? "rotate" : ""} />
              </button>
            )}
          </div>

          <div className="request-user-section">
            <h2>Despre solicitant</h2>
            <div className="user-card">
              <div className="user-info">
                <Avatar
                  src={getProfileImageUrl(request.user)}
                  name={request.user?.name}
                  size="xl"
                  bg={!getProfileImageUrl(request.user) ? "blue.500" : undefined}
                  color="white"
                  className="user-image"
                />
                <div className="user-details">
                  <div className="user-name-wrapper">
                    <h3>{request.user?.name}</h3>
                    {request.user?.verified && (
                      <span className="verified-badge" title="Utilizator verificat">
                        <Check size={14} />
                      </span>
                    )}
                  </div>
                  <div className="user-rating">
                    <Star size={16} fill="#ffc939" color="#ffc939" />
                    <span>
                      {request.user?.rating || "N/A"} ({request.user?.reviewCount || 0} recenzii)
                    </span>
                  </div>
                </div>
              </div>
              {!isOwnRequest && (
                <div className="user-actions">
                  <Link to={`/chat/${request.user?.id}`} className="contact-button">
                    <MessageCircle size={20} />
                    Contactează
                  </Link>
                  <Link to={`/profile/${request.user?.id}`} className="view-profile-button">
                    Vezi profil
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
        
        <div className="request-sidebar">
          {renderNegotiationSidebar()}
          
          <div className="client-mini-card">
            <Link to={`/profile/${request.user?.id}`} className="client-mini-info">
              <Avatar
                src={getProfileImageUrl(request.user)}
                name={request.user?.name}
                size="md"
                bg={!getProfileImageUrl(request.user) ? "blue.500" : undefined}
                color="white"
                className="client-mini-image"
              />
              <div>
                <div className="client-mini-name">
                  {request.user?.name}
                  {request.user?.verified && (
                    <div className="verified-mini-badge">
                      <Check size={10} />
                    </div>
                  )}
                </div>
                {request.user?.rating && (
                  <div className="client-mini-rating">
                    <Star size={14} fill="#ffc939" color="#ffc939" />
                    <span>{request.user?.rating}</span>
                  </div>
                )}
              </div>
            </Link>
            <div className="response-time">
              <Clock size={14} />
              <span>De obicei răspunde în 24 ore</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default RequestDetailsPage
