import React, { useState, useEffect } from 'react';
import { Flex, useToast } from '@chakra-ui/react';
import { Chat } from '../components/Chat/Chat';
import { ChatList } from '../components/Chat/ChatList';
import { useSocket } from '../contexts/SocketContext';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import './ChatPage.css';

export function ChatPage() {
  const [selectedChat, setSelectedChat] = useState(null);
  const [chats, setChats] = useState([]);
  const { socket } = useSocket();
  const { user } = useAuth();
  const toast = useToast();

  const fetchChats = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/chats`);
      setChats(response.data);
    } catch (error) {
      console.error('Error fetching chats:', error);
      toast({
        title: 'Error loading chats',
        status: 'error',
        duration: 3000,
      });
    }
  };

  useEffect(() => {
    fetchChats();
  }, [toast]); // Added toast as dependency

  useEffect(() => {
    if (socket) {
      socket.on('new_message', (message) => {
        // Actualizează lista de chat-uri când primești un mesaj nou
        setChats(prevChats => {
          const chatIndex = prevChats.findIndex(
            chat => chat.userId === message.sender_id || chat.userId === message.receiver_id
          );

          if (chatIndex === -1) {
            // Adaugă un nou chat dacă nu există
            return [{
              userId: message.sender_id === user.id ? message.receiver_id : message.sender_id,
              lastMessage: message.content,
              timestamp: message.created_at,
              unreadCount: message.sender_id !== user.id ? 1 : 0
            }, ...prevChats];
          }

          // Actualizează chat-ul existent
          const updatedChats = [...prevChats];
          updatedChats[chatIndex] = {
            ...updatedChats[chatIndex],
            lastMessage: message.content,
            timestamp: message.created_at,
            unreadCount: message.sender_id !== user.id ? 
              (updatedChats[chatIndex].unreadCount || 0) + 1 : 
              updatedChats[chatIndex].unreadCount
          };

          return updatedChats;
        });
      });

      return () => {
        socket.off('new_message');
      };
    }
  }, [socket, user]);

  return (
    <div className="chat-page">
      <div className="chat-sidebar">
        <ChatList
          chats={chats}
          selectedChat={selectedChat}
          onSelectChat={setSelectedChat}
        />
      </div>
      <div className="chat-main">
        {selectedChat ? (
          <Chat
            receiverId={selectedChat.userId}
            receiverName={selectedChat.userName}
          />
        ) : (
          <Flex
            justify="center"
            align="center"
            h="100%"
            color="gray.500"
          >
            Select a chat to start messaging
          </Flex>
        )}
      </div>
    </div>
  );
}
