import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import { getConversations, markAllMessagesRead } from '../services/messageApi';
import Chat from '../components/Chat';
import { Avatar, Badge, Box, HStack, VStack, Text, Input, InputGroup, InputLeftElement } from '@chakra-ui/react';
import { Search, ArrowLeft } from 'lucide-react';
import './ChatPage.css';

// Array of colors for avatars
const avatarColors = [
  "red.500", "orange.500", "yellow.500", "green.500", "teal.500", 
  "blue.500", "cyan.500", "purple.500", "pink.500", "linkedin.500"
];

// Function to get a deterministic color based on user name
const getAvatarColor = (name) => {
  if (!name) return "gray.500";

  // Get a simple hash of the name
  let hash = 0;
  for (let i = 0; i < name.length; i++) {
    hash = name.charCodeAt(i) + ((hash << 5) - hash);
  }

  // Use the hash to pick a color
  const index = Math.abs(hash % avatarColors.length);
  return avatarColors[index];
};

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const { socket, connected } = useSocket();
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [markingAsRead, setMarkingAsRead] = useState(false);

  // Load conversations
  useEffect(() => {
    const fetchChats = async () => {
      try {
        setLoading(true);
        const data = await getConversations();

        // Verificăm dacă avem date valide
        if (!data || !Array.isArray(data)) {
          setConversations([]);
          setError('Nu s-au putut încărca conversațiile');
          setLoading(false);
          return;
        }

        // Nu mai filtrăm conversațiile, deoarece backend-ul ar trebui să returneze doar conversațiile utilizatorului curent
        setConversations(data);
        setError(null);

        // Check if we have a receiverId in the location state (from profile page)
        const receiverId = location.state?.receiverId;

        // If we have a receiverId in state, navigate to that chat
        if (receiverId && !userId) {
          navigate(`/chat/${receiverId}`);
          return;
        }
      } catch (err) {
        console.error('Error fetching chats:', err);
        setError('Failed to load conversations');
      } finally {
        setLoading(false);
      }
    };

    fetchChats();
  }, [userId, navigate, user.id, location.state]);

  // Mark messages as read when a user opens a conversation
  useEffect(() => {
    const markMessagesAsRead = async () => {
      if (!userId || markingAsRead) return;

      try {
        setMarkingAsRead(true);
        // Mark all messages from this sender as read
        await markAllMessagesRead(userId);

        // Update the conversations list to reflect the read status
        const updatedConversations = await getConversations();
        setConversations(updatedConversations);
      } catch (err) {
        console.error('Error marking messages as read:', err);
      } finally {
        setMarkingAsRead(false);
      }
    };

    markMessagesAsRead();
  }, [userId]);

  // Listen for new messages to update conversations list
  useEffect(() => {
    if (!socket || !connected) return;

    const handleMessageReceived = async (message) => {
      // If this message is relevant to the current user (sender or receiver)
      if (message.sender_id === user.id || message.receiver_id === user.id) {
        // If the message is from the current open conversation, mark it as read immediately
        if (message.sender_id === parseInt(userId) && message.receiver_id === user.id) {
          try {
            await markAllMessagesRead(userId);
          } catch (err) {
            console.error('Error marking message as read:', err);
          }
        }

        // Refresh the conversations list
        try {
          const data = await getConversations();
          if (data && Array.isArray(data)) {
            setConversations(data);
          }
        } catch (err) {
          console.error('Error refreshing conversations after new message:', err);
        }
      }
    };

    socket.on('message_received', handleMessageReceived);

    return () => {
      socket.off('message_received', handleMessageReceived);
    };
  }, [socket, connected, user.id, userId]);

  // Filter conversations by search query
  const filteredConversations = useMemo(() => {
    if (!searchQuery.trim()) return conversations;

    return conversations.filter(conv => 
      conv.user.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [conversations, searchQuery]);

  if (loading) {
    return <Box className="chat-page-loading">Loading chats...</Box>;
  }

  if (error) {
    return <Box className="chat-page-error">{error}</Box>;
  }

  return (
    <Box className="chat-page" h="100vh" bg="gray.50">
      {userId ? (
        // Chat view with conversation open
        <Box h="100%">
          <HStack
            p={4}
            bg="white"
            borderBottomWidth="1px"
            borderColor="gray.200"
            position="sticky"
            top={0}
            zIndex={1}
          >
            <Box 
              as="button"
              onClick={() => navigate('/chat')}
              aria-label="Back to chat list"
              display="flex"
              alignItems="center"
              justifyContent="center"
              borderRadius="full"
              p={2}
              _hover={{ bg: "gray.100" }}
            >
              <ArrowLeft size={24} />
            </Box>
            <Text fontWeight="bold">
              {conversations.find(c => c.user.id === parseInt(userId))?.user.name || 'Chat'}
            </Text>
          </HStack>

          <Box h="calc(100% - 60px)">
            <Chat 
              otherUser={conversations.find(c => c.user.id === parseInt(userId))?.user}
            />
          </Box>
        </Box>
      ) : (
        // Conversations list view
        <Box h="100%">
          <Box
            p={4}
            bg="white"
            borderBottomWidth="1px"
            borderColor="gray.200"
            position="sticky"
            top={0}
            zIndex={1}
          >
            <HStack mb={4}>
              <Box 
                as="button"
                onClick={() => navigate('/home')}
                aria-label="Back to homepage"
                display="flex"
                alignItems="center"
                justifyContent="center"
                borderRadius="full"
                p={2}
                _hover={{ bg: "gray.100" }}
              >
                <ArrowLeft size={24} />
              </Box>
              <Text fontSize="xl" fontWeight="bold">
                Messages
              </Text>
            </HStack>

            <InputGroup>
              <InputLeftElement pointerEvents="none">
                <Search size={18} color="gray.400" />
              </InputLeftElement>
              <Input
                placeholder="Search conversations..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                borderRadius="full"
                bg="gray.100"
                _focus={{ bg: "white", borderColor: "blue.500", boxShadow: "0 0 0 1px blue.500" }}
              />
            </InputGroup>
          </Box>

          <VStack spacing={0} align="stretch" pb={4} overflowY="auto" h="calc(100% - 110px)">
            {filteredConversations.length === 0 ? (
              <Box p={4} textAlign="center" color="gray.500">
                {searchQuery ? (
                  <Text>No conversations matching "{searchQuery}"</Text>
                ) : (
                  <VStack spacing={2}>
                    <Text>No conversations yet</Text>
                    <Text fontSize="sm" opacity={0.7}>Start a chat from a user's profile</Text>
                  </VStack>
                )}
              </Box>
            ) : (
              filteredConversations.map((conv) => (
                <Box
                  key={conv.user.id}
                  p={4}
                  bg="white"
                  borderBottomWidth="1px"
                  borderColor="gray.100"
                  cursor="pointer"
                  onClick={() => navigate(`/chat/${conv.user.id}`)}
                  _hover={{ bg: "gray.50" }}
                >
                  <HStack spacing={4}>
                    <Box position="relative">
                      <Avatar
                        size="md"
                        name={conv.user.name} // Acest lucru va afișa inițialele când nu există imagine
                        src={conv.user.image}
                        bg={getAvatarColor(conv.user.name)} // Culoare determinată în funcție de nume
                      />
                      {conv.user.isOnline && (
                        <Badge
                          position="absolute"
                          bottom="0"
                          right="0"
                          bg="green.400"
                          borderRadius="full"
                          transform="translate(20%, 20%)"
                          boxSize="3"
                        />
                      )}
                    </Box>

                    <Box flex="1">
                      <HStack justify="space-between" mb={1}>
                        <Text fontWeight="bold">
                          {conv.user.name}
                        </Text>
                        <Text fontSize="sm" color="gray.500">
                          {formatTimestamp(conv.lastMessage.time)}
                        </Text>
                      </HStack>

                      <HStack justify="space-between">
                        <Text
                          fontSize="sm"
                          color="gray.600"
                          noOfLines={1}
                          flex="1"
                        >
                          {conv.lastMessage.sender === 'you' && 'You: '}
                          {conv.lastMessage.text}
                        </Text>

                        {conv.unreadCount > 0 && (
                          <Badge
                            colorScheme="blue"
                            borderRadius="full"
                            px={2}
                          >
                            {conv.unreadCount}
                          </Badge>
                        )}
                      </HStack>
                    </Box>
                  </HStack>
                </Box>
              ))
            )}
          </VStack>
        </Box>
      )}
    </Box>
  );
};

// Helper function to format timestamp
const formatTimestamp = (timestamp) => {
  const date = new Date(timestamp);
  const now = new Date();
  const diff = now - date;

  // If less than 24 hours, show only time
  if (diff < 24 * 60 * 60 * 1000) {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  // If less than a week, show day name
  if (diff < 7 * 24 * 60 * 60 * 1000) {
    return date.toLocaleDateString([], { weekday: 'short' });
  }

  // Otherwise show date
  return date.toLocaleDateString([], { month: 'short', day: 'numeric' });
};

export default ChatPage;
