                    <HStack justify="space-between">
                      <Text
                        fontSize="sm"
                        color="gray.600"
                        noOfLines={1}
                        flex="1"
                      >
                        {conv.lastMessage.sender === 'you' && 'You: '}
                        {conv.lastMessage.text}
                      </Text>

                      {conv.unreadCount > 0 && (
                        <Badge
                          colorScheme="blue"
                          borderRadius="full"
                          px={2}
