      </Box>

      {/* Zona de chat 칥n dreapta - vizibil캒 doar c칙nd userId exist캒 */}
      {userId ? (
        <Box 
          flex={1} 
          h="100%" 
          display={{ base: "block", md: "block" }}
        >
          <HStack
            p={4}
            bg="white"
            borderBottomWidth="1px"
            borderColor="gray.200"
