"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { Search, Bell, Menu, X, MessageCircle, LogOut, User, Settings } from "lucide-react"
import { useAuth } from "../contexts/AuthContext"
import { useMessages } from "../contexts/MessageContext"
import { getProfileImageUrl, handleImageError, getImageUrl } from "../utils/imageUtils"
import "./Header.css"
import logoImage from "../assets/images/favicon.png"
import { Avatar } from '@chakra-ui/react'

const Header = () => {
  const { user, logoutUser } = useAuth()
  const { unreadCount: unreadMessages } = useMessages()
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [isSearchFocused, setIsSearchFocused] = useState(false)
  const [searchQuery, setSearchQuery] = useState("")

  // Debug the profile image
  console.log("User in Header:", user);
  console.log("User profile image properties:", {
    profileImage: user?.profileImage,
    profile_image: user?.profile_image,
    image: user?.image
  });
  
  // Get user's display name - use different property combinations based on what's available
  const getUserName = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    } else if (user?.first_name && user?.last_name) {
      return `${user.first_name} ${user.last_name}`;
    } else if (user?.name) {
      return user.name;
    } else {
      return 'User';
    }
  };

  // When the backend returns user data, the profile image is in profile_image, not profileImage
  // We need to check all possible properties
  const profileImagePath = user?.profile_image || user?.profileImage || user?.image;

  // Helper function to get the correct image URL or null for Avatar component
  // Using null instead of defaultProfileImage to let Chakra Avatar display initials when no image
  const getAvatarImageUrl = (path) => {
    if (!path) return null;
    return getImageUrl(path);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen)
  }

  // Function for logout
  const handleLogout = (e) => {
    e.preventDefault()
    logoutUser()
    toggleMobileMenu() // Close mobile menu if open
  }

  return (
    <header className="app-header">
      <div className="header-container">
        {/* Logo */}
        <div className="header-logo">
          <Link to="/" className="logo-link">
            <img src={logoImage || "/placeholder.svg"} alt="Logo" className="logo-image" />
            <span className="logo-text">TUDU</span>
          </Link>
        </div>

        {/* Search Bar */}
        <div className={`header-search ${isSearchFocused ? "focused" : ""}`}>
          <div className="search-container">
            <Search size={20} className="search-icon" />
            <input
              type="text"
              placeholder="Caută servicii..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onFocus={() => setIsSearchFocused(true)}
              onBlur={() => setIsSearchFocused(false)}
              className="search-input"
            />
            {searchQuery && (
              <button className="clear-search" onClick={() => setSearchQuery("")} aria-label="Clear search">
                <X size={16} />
              </button>
            )}
          </div>
        </div>

        {/* Navigation */}
        <nav className="header-nav">
          <Link to="/requests" className="nav-icon-link requests-link" title="Cereri">
            Cereri
          </Link>
          <Link to="/chat" className="nav-icon-link" title="Mesaje">
            <MessageCircle size={24} />
            {unreadMessages > 0 && (
              <span className="notification-badge">{unreadMessages > 99 ? '99+' : unreadMessages}</span>
            )}
          </Link>
          <Link to="/notifications" className="nav-icon-link" title="Notificări">
            <Bell size={24} />
            <span className="notification-badge">5</span>
          </Link>
          <Link to="/profile" className="profile-link">
            <Avatar 
              src={getAvatarImageUrl(profileImagePath)}
              name={getUserName()}
              size="sm"
              bg={!profileImagePath ? "blue.500" : undefined}
              color="white"
              className="profile-image" 
            />
          </Link>

          {/* Mobile Menu Button */}
          <button
            className="mobile-menu-button"
            onClick={toggleMobileMenu}
            aria-label={mobileMenuOpen ? "Close menu" : "Open menu"}
          >
            {mobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </nav>
      </div>

      {/* Mobile Menu */}
      <div className={`mobile-menu ${mobileMenuOpen ? "open" : ""}`}>
        <div className="mobile-menu-content">
          <div className="mobile-menu-header">
            <Avatar 
              src={getAvatarImageUrl(profileImagePath)}
              name={getUserName()}
              size="md"
              bg={!profileImagePath ? "blue.500" : undefined}
              color="white"
              className="mobile-profile-image" 
            />
            <div className="mobile-profile-info">
              <h3>{getUserName()}</h3>
              <p>Vezi profilul tău</p>
            </div>
          </div>
          <nav className="mobile-nav">
            <Link to="/profile" onClick={toggleMobileMenu}>Profilul meu</Link>
            <Link to="/settings" onClick={toggleMobileMenu}>Setări</Link>
            <button onClick={handleLogout} className="logout-button">
              Deconectare
            </button>
          </nav>
        </div>
      </div>
    </header>
  )
}

export default Header
